package application;

public class AllClints {
	String ClintName;
	String ClintBalance;
	public AllClints() {
		super();
		// TODO Auto-generated constructor stub
	}
	public AllClints(String clintName, String clintBalance) {
		super();
		ClintName = clintName;
		ClintBalance = clintBalance;
	}
	public String getClintName() {
		return ClintName;
	}
	public void setClintName(String clintName) {
		ClintName = clintName;
	}
	public String getClintBalance() {
		return ClintBalance;
	}
	public void setClintBalance(String clintBalance) {
		ClintBalance = clintBalance;
	}
	

}
